import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import Axios from "axios";

const Slider = () => {
    const [data, setData] = useState([]);


    
    const handleDelete = (id) => {
        // Configurer l'URL de l'API
        const apiUrl = `http://localhost:8080/participant/delete/${id}`;
      
        // Configurer l'objet de la requête DELETE
        const requestOptions = {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            // Ajouter des en-têtes supplémentaires si nécessaire
          },
        };
      
        // Effectuer la requête DELETE avec fetch
        fetch(apiUrl, requestOptions)
          .then((response) => {
            if (!response.ok) {
              throw new Error(`Erreur HTTP ! Statut : ${response.status}`);
            }
            console.log("Utilisateur supprimé avec succès !");
            // Mettre à jour les données après la suppression
            // Vous pouvez choisir de rafraîchir la page ou de recharger les données ici
          })
          .catch((error) => console.error("Erreur lors de la suppression de l'utilisateur :", error));
      };
      






    useEffect(() => {
      // Effectuer une requête GET à l'URL de votre backend
      Axios.get("http://localhost:8080/participant/get_all")
        .then((response) => setData(response.data))
        .catch((error) => console.error("Erreur lors de la récupération des données :", error));
    }, []);
   
  return (
    <>


  
  <li class="nav-item">
          <a class="nav-link text-white active bg-gradient-primary" href="../pages/dashboard.html">
            <div class="text-white text-center me-2 d-flex align-items-center justify-content-center">
        
            </div>
            <span class="nav-link-text ms-1">liste des participant </span>
          </a>
        </li>
      
        <div>
      <h1>Table des participants</h1>
      <table border="1">
      <thead>
        <tr>
          <th>ID</th>
          <th>Nom</th>
          <th>Prénom</th>
          <th>E-mail</th>
          <th>Fonction</th>
          <th>Somme</th>
        </tr>
      </thead>
      <tbody>
      {data.map((item) => (
          <tr key={item.id}>
            <td>{item.id}</td>
            <td>{item.nom}</td>
            <td>{item.prenom}</td>
            <td>{item.email}</td>
            <td>{item.fonction}</td>
            <td>{item.somme}</td>
            <td>
              <button onClick={() => handleDelete(item.id)}>Supprimer</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
    </div>


   </>)}
   export default Slider;
